import React, { useEffect, useState } from 'react';
import * as Notifications from 'expo-notifications';
import { Platform } from 'react-native';
import { userAPI } from './services/api';

let Device = null;
if (Platform.OS !== 'web') {
  try {
    Device = require('expo-device');
  } catch (e) {
    // dynamic require failed (e.g. running in web bundler) ‚Äî leave Device null
    Device = null;
  }
}
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import AsyncStorage from '@react-native-async-storage/async-storage';
import LoginScreen from './screens/auth/LoginScreen';
import RegisterScreen from './screens/auth/RegisterScreen';
import CustomerHomeScreen from './screens/customer/CustomerHomeScreen';
import RestaurantsScreen from './screens/customer/RestaurantsScreen';
import GroceriesScreen from './screens/customer/GroceriesScreen';
import ShoppingScreen from './screens/customer/ShoppingScreen';
import BrandProductsScreen from './screens/customer/BrandProductsScreen';
import CustomerOrdersScreen from './screens/customer/CustomerOrdersScreen';
import CustomerOrderTrackingScreen from './screens/customer/CustomerOrderTrackingScreen';
import RiderHomeScreen from './screens/rider/RiderHomeScreen';
import RiderActiveScreen from './screens/rider/RiderActiveScreen';
import AdminDashboardScreen from './screens/admin/AdminDashboardScreen';
import AdminTicketsScreen from './screens/admin/AdminTicketsScreen';
import { ActivityIndicator, View, Text } from 'react-native';

const Stack = createStackNavigator();
const Tab = createBottomTabNavigator();

// Auth Stack per non autenticati
function AuthStack() {
  return (
    <Stack.Navigator
      screenOptions={{
        headerShown: false,
        animationEnabled: true
      }}
    >
      <Stack.Screen name="Login" component={LoginScreen} />
      <Stack.Screen name="Register" component={RegisterScreen} />
    </Stack.Navigator>
  );
}

// Customer Stack
function CustomerTabs() {
  return (
    <Tab.Navigator
      screenOptions={{
        headerShown: true,
        tabBarActiveTintColor: '#FF6B00',
        tabBarInactiveTintColor: '#999',
        headerStyle: {
          backgroundColor: '#FF6B00',
        },
        headerTintColor: '#fff',
        headerTitleStyle: {
          fontWeight: 'bold',
        },
      }}
    >
      <Tab.Screen
        name="Home"
        component={CustomerHomeScreen}
        options={{
          title: 'üçî Home',
          tabBarLabel: 'üçî Home',
          tabBarIcon: ({ color }) => <Text style={{ fontSize: 20 }}>üè†</Text>,
        }}
      />
      <Tab.Screen
        name="Restaurants"
        component={RestaurantsScreen}
        options={{
          title: 'üçΩÔ∏è Ristoranti',
          tabBarLabel: 'üçΩÔ∏è Ristoranti',
          tabBarIcon: ({ color }) => <Text style={{ fontSize: 20 }}>üçΩÔ∏è</Text>,
        }}
      />
      <Tab.Screen
        name="Groceries"
        component={GroceriesScreen}
        options={{
          title: 'üõí Spesa',
          tabBarLabel: 'üõí Spesa',
          tabBarIcon: ({ color }) => <Text style={{ fontSize: 20 }}>üõí</Text>,
        }}
      />
      <Tab.Screen
        name="Shopping"
        component={ShoppingScreen}
        options={{
          title: 'üõçÔ∏è Shopping',
          tabBarLabel: 'üõçÔ∏è Shopping',
          tabBarIcon: ({ color }) => <Text style={{ fontSize: 20 }}>üõçÔ∏è</Text>,
        }}
      />
      <Tab.Screen
        name="Orders"
        component={CustomerOrdersScreen}
        options={{
          title: 'üì¶ Ordini',
          tabBarLabel: 'üì¶ Ordini',
          tabBarIcon: ({ color }) => <Text style={{ fontSize: 20 }}>üì¶</Text>,
        }}
      />
    </Tab.Navigator>
  );
}

function CustomerStack() {
  return (
    <Stack.Navigator>
      <Stack.Screen
        name="CustomerTabs"
        component={CustomerTabs}
        options={{ headerShown: false }}
      />
      <Stack.Screen
        name="BrandProducts"
        component={BrandProductsScreen}
        options={{ title: 'Prodotti' }}
      />
      <Stack.Screen
        name="OrderTracking"
        component={CustomerOrderTrackingScreen}
        options={{
          title: 'üìç Tracciamento Ordine',
          headerStyle: {
            backgroundColor: '#FF6B00',
          },
          headerTintColor: '#fff',
          headerTitleStyle: {
            fontWeight: 'bold',
          },
        }}
      />
    </Stack.Navigator>
  );
}

// Rider Stack
function RiderStack() {
  return (
    <Tab.Navigator
      screenOptions={{
        headerShown: true,
        tabBarActiveTintColor: '#0066FF',
        tabBarInactiveTintColor: '#999',
        headerStyle: {
          backgroundColor: '#0066FF',
        },
        headerTintColor: '#fff',
        headerTitleStyle: {
          fontWeight: 'bold',
        },
      }}
    >
      <Tab.Screen
        name="Available"
        component={RiderHomeScreen}
        options={{
          title: 'üì¶ Ordini Disponibili',
          tabBarLabel: 'üì¶ Disponibili',
          tabBarIcon: ({ color }) => <Text style={{ fontSize: 20 }}>üì¶</Text>,
        }}
      />
      <Tab.Screen
        name="Active"
        component={RiderActiveScreen}
        options={{
          title: 'üöö Mie Consegne',
          tabBarLabel: 'üöö Consegne',
          tabBarIcon: ({ color }) => <Text style={{ fontSize: 20 }}>üöö</Text>,
        }}
      />
    </Tab.Navigator>
  );
}

// Manager Stack - Admin Dashboard
function ManagerStack({ token, user }) {
  return (
    <Stack.Navigator
      screenOptions={{
        headerShown: true,
        headerStyle: {
          backgroundColor: '#FF6B00',
        },
        headerTintColor: '#fff',
        headerTitleStyle: {
          fontWeight: 'bold',
        },
      }}
    >
      <Stack.Screen
        name="AdminDashboard"
        component={AdminDashboardScreen}
        options={{
          title: '‚öôÔ∏è Admin Dashboard',
        }}
        initialParams={{ token, user }}
      />
      <Stack.Screen
        name="AdminTickets"
        component={AdminTicketsScreen}
        options={{
          title: 'üé´ Tickets',
        }}
        initialParams={{ token }}
      />
    </Stack.Navigator>
  );
}

export default function App() {
  const [state, dispatch] = React.useReducer(
    (prevState, action) => {
      switch (action.type) {
        case 'RESTORE_TOKEN':
          return {
            ...prevState,
            userToken: action.token,
            user: action.user,
            isLoading: false,
          };
        case 'SIGN_IN':
          return {
            ...prevState,
            isSignout: false,
            userToken: action.token,
            user: action.user,
          };
        case 'SIGN_OUT':
          return {
            ...prevState,
            isSignout: true,
            userToken: null,
            user: null,
          };
      }
    },
    {
      isLoading: true,
      isSignout: false,
      userToken: null,
      user: null,
    }
  );

  useEffect(() => {
    const bootstrapAsync = async () => {
      let token;
      let user;
      try {
        token = await AsyncStorage.getItem('token');
        user = await AsyncStorage.getItem('user');
        if (user) user = JSON.parse(user);

        if (token && user) {
          console.log('‚úÖ Token trovato in AsyncStorage:', token.substring(0, 20) + '...');
          console.log('‚úÖ User trovato:', user);
        }
      } catch (e) {
        // Restoring token failed
        console.error('Error restoring token:', e);
      }

      dispatch({ type: 'RESTORE_TOKEN', token, user });
    };

    bootstrapAsync();

    // Try to register push token for logged in user
    const registerPush = async () => {
      try {
        if (Device && !Device.isDevice) {
          console.log('Push notifications require a physical device');
          return;
        }

        if (!Device) {
          console.log('Push notifications on web - skipping native device check');
        }
        const { status: existingStatus } = await Notifications.getPermissionsAsync();
        let finalStatus = existingStatus;
        if (existingStatus !== 'granted') {
          const { status } = await Notifications.requestPermissionsAsync();
          finalStatus = status;
        }
        if (finalStatus !== 'granted') {
          console.log('Failed to get push token permission');
          return;
        }
        const tokenData = await Notifications.getExpoPushTokenAsync();
        const token = tokenData.data;
        console.log('Got push token:', token);
        await AsyncStorage.setItem('push_token', token);
        // send to backend
        try {
          await userAPI.setPushToken(token);
        } catch (e) {
          console.warn('Could not send push token to backend', e.message);
        }
      } catch (e) {
        console.warn('registerPush error', e.message);
      }
    };

    registerPush();

    // Polling interval per controllare se il token √® stato salvato (importante per web)
    const pollInterval = setInterval(() => {
      bootstrapAsync();
    }, 1000);

    return () => clearInterval(pollInterval);
  }, []);

  if (state.isLoading) {
    return (
      <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
        <ActivityIndicator size="large" color="#FF6B00" />
      </View>
    );
  }

  return (
    <NavigationContainer>
      {state.userToken == null ? (
        <AuthStack />
      ) : state.user?.role === 'customer' ? (
        <>
          {console.log('üéØ Navigando a CustomerStack per role:', state.user?.role)}
          <CustomerStack />
        </>
      ) : state.user?.role === 'rider' ? (
        <>
          {console.log('üéØ Navigando a RiderStack per role:', state.user?.role)}
          <RiderStack />
        </>
      ) : state.user?.role === 'manager' || state.user?.role === 'admin' ? (
        <>
          {console.log('üéØ Navigando a ManagerStack per role:', state.user?.role)}
          <ManagerStack token={state.userToken} user={state.user} />
        </>
      ) : (
        <>
          {console.log('‚ùå Ruolo sconosciuto:', state.user?.role)}
          <AuthStack />
        </>
      )}
    </NavigationContainer>
  );
}
