import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  StyleSheet,
  FlatList,
  Alert,
  ActivityIndicator,
  Modal,
  TextInput,
  RefreshControl,
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { ordersAPI } from '../../services/api';

const CATEGORY_EMOJI = {
  food: 'ğŸ”',
  pharmacy: 'ğŸ’Š',
  groceries: 'ğŸ›’',
  clothes: 'ğŸ‘•',
  electronics: 'ğŸ’»',
  restaurants: 'ğŸ½ï¸',
};

const STATUS_CONFIG = {
  accepted: { color: '#0066FF', label: 'âœ“ Accettato', icon: 'ğŸ“Œ' },
  pickup: { color: '#FFA500', label: 'ğŸ“¦ Ritiro', icon: 'ğŸ“¦' },
  in_transit: { color: '#FFD700', label: 'ğŸš— In Consegna', icon: 'ğŸš—' },
  delivered: { color: '#28A745', label: 'âœ… Consegnato', icon: 'âœ…' },
  rated: { color: '#27AE60', label: 'â­ Valutato', icon: 'â­' },
  pending: { color: '#FFC107', label: 'â³ In Attesa', icon: 'â³' },
};

export default function RiderActiveScreen({ navigation }) {
  const [orders, setOrders] = useState([]);
  const [activeTab, setActiveTab] = useState('active');
  const [stats, setStats] = useState({
    active: 0,
    completed: 0,
    earnings: 0,
    avgRating: 4.7,
  });
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [ratingModalVisible, setRatingModalVisible] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [rating, setRating] = useState(0);
  const [ratingNote, setRatingNote] = useState('');

  useEffect(() => {
    loadActiveOrders();
    const interval = setInterval(loadActiveOrders, 10000);
    return () => clearInterval(interval);
  }, []);

  const loadActiveOrders = async () => {
    try {
      setRefreshing(true);
      const response = await ordersAPI.getMyOrders();
      const activeOrders = (response || []).filter(
        order => order.status !== 'delivered' && order.status !== 'rated'
      );
      const completedOrders = (response || []).filter(
        order => order.status === 'delivered' || order.status === 'rated'
      );
      
      setOrders(response || []);
      setStats({
        active: activeOrders.length,
        completed: completedOrders.length,
        earnings: completedOrders.reduce((sum, o) => sum + (o.total_amount * 0.15 || 0), 0),
        avgRating: 4.7,
      });
    } catch (error) {
      console.error('Error loading active orders:', error);
    } finally {
      setRefreshing(false);
      setLoading(false);
    }
  };

  const updateOrderStatus = async (orderId, newStatus) => {
    try {
      await ordersAPI.updateOrderStatus(orderId, newStatus);
      Alert.alert('âœ… Aggiornato', `Stato: ${STATUS_CONFIG[newStatus].label}`);
      loadActiveOrders();
    } catch (error) {
      Alert.alert('Errore', 'Impossibile aggiornare lo stato');
    }
  };

  const handleCompleteOrder = (order) => {
    Alert.alert(
      'âœ… Consegna Completata?',
      `Confermi di aver consegnato a ${order.customer_name}?`,
      [
        { text: 'Annulla', style: 'cancel' },
        {
          text: 'SÃ¬, Completa',
          onPress: async () => {
            await updateOrderStatus(order.id, 'delivered');
            setSelectedOrder(order);
            setRatingModalVisible(true);
          },
          style: 'default',
        },
      ]
    );
  };

  const submitRating = async () => {
    if (rating === 0) {
      Alert.alert('Attenzione', 'Seleziona una valutazione');
      return;
    }
    
    try {
      await ordersAPI.rateOrder(selectedOrder.id, {
        rating,
        notes: ratingNote,
      });
      Alert.alert('âœ… Grazie!', `Ordine valutato ${rating}â­`);
      setRatingModalVisible(false);
      setRating(0);
      setRatingNote('');
      loadActiveOrders();
    } catch (error) {
      Alert.alert('Errore', 'Impossibile salvare la valutazione');
    }
  };

  const handleLogout = async () => {
    await AsyncStorage.removeItem('token');
    await AsyncStorage.removeItem('user');
  };

  const handleCallCustomer = (orderId) => {
    Alert.alert('ğŸ“ Chiama Cliente', `Inizia una chiamata all'ordine ${orderId}?`);
  };

  const getFilteredOrders = () => {
    if (activeTab === 'active') {
      return orders.filter(o => 
        o.status === 'accepted' || o.status === 'pickup' || o.status === 'in_transit'
      );
    } else if (activeTab === 'completed') {
      return orders.filter(o => o.status === 'delivered' || o.status === 'rated');
    }
    return [];
  };

  if (loading) {
    return (
      <View style={styles.centerContainer}>
        <ActivityIndicator size="large" color="#0066FF" />
        <Text style={styles.loadingText}>Caricamento...</Text>
      </View>
    );
  }

  const filteredOrders = getFilteredOrders();

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <View>
          <Text style={styles.welcomeText}>Le Mie Consegne ğŸ“¦</Text>
          <Text style={styles.statsHeader}>
            {stats.active} attive â€¢ {stats.completed} completate
          </Text>
        </View>
        <TouchableOpacity style={styles.logoutButton} onPress={handleLogout}>
          <Text style={styles.logoutText}>ğŸšª</Text>
        </TouchableOpacity>
      </View>

      {/* Stats Cards */}
      <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.statsContainer}>
        <View style={styles.statCard}>
          <Text style={styles.statIcon}>ğŸš—</Text>
          <Text style={styles.statValue}>{stats.active}</Text>
          <Text style={styles.statLabel}>Attive</Text>
        </View>
        
        <View style={styles.statCard}>
          <Text style={styles.statIcon}>âœ…</Text>
          <Text style={styles.statValue}>{stats.completed}</Text>
          <Text style={styles.statLabel}>Completate</Text>
        </View>
        
        <View style={styles.statCard}>
          <Text style={styles.statIcon}>ğŸ’°</Text>
          <Text style={styles.statValue}>â‚¬{stats.earnings.toFixed(2)}</Text>
          <Text style={styles.statLabel}>Guadagni</Text>
        </View>

        <View style={styles.statCard}>
          <Text style={styles.statIcon}>â­</Text>
          <Text style={styles.statValue}>{stats.avgRating}</Text>
          <Text style={styles.statLabel}>Rating</Text>
        </View>
      </ScrollView>

      {/* Tabs */}
      <View style={styles.tabContainer}>
        <TouchableOpacity
          style={[styles.tab, activeTab === 'active' && styles.activeTab]}
          onPress={() => setActiveTab('active')}
        >
          <Text style={styles.tabText}>Attive</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.tab, activeTab === 'completed' && styles.activeTab]}
          onPress={() => setActiveTab('completed')}
        >
          <Text style={styles.tabText}>Completate</Text>
        </TouchableOpacity>
      </View>

      {/* Orders List */}
      {filteredOrders.length === 0 ? (
        <View style={styles.emptyContainer}>
          <Text style={styles.emptyIcon}>
            {activeTab === 'active' ? 'ğŸ‰' : 'ğŸ“­'}
          </Text>
          <Text style={styles.emptyText}>
            {activeTab === 'active' ? 'Nessuna consegna in corso!' : 'Nessun completamento'}
          </Text>
          <Text style={styles.emptySubtext}>
            {activeTab === 'active' 
              ? "Vai alla schermata 'Home' per accettare ordini"
              : 'I tuoi ordini completati appariranno qui'}
          </Text>
        </View>
      ) : (
        <FlatList
          data={filteredOrders}
          keyExtractor={item => item.id?.toString() || Math.random().toString()}
          renderItem={({ item }) => {
            const statusInfo = STATUS_CONFIG[item.status] || STATUS_CONFIG.pending;
            return (
              <View style={styles.orderCard}>
                {/* Order Header */}
                <View style={styles.orderHeader}>
                  <View style={styles.categoryBadge}>
                    <Text style={styles.categoryEmoji}>
                      {CATEGORY_EMOJI[item.category] || 'ğŸ“¦'}
                    </Text>
                  </View>
                  <View style={styles.orderInfo}>
                    <Text style={styles.orderDescription} numberOfLines={2}>
                      {item.description || 'Ordine'}
                    </Text>
                    <View style={[styles.statusBadge, { backgroundColor: statusInfo.color }]}>
                      <Text style={styles.statusText}>
                        {statusInfo.icon} {statusInfo.label}
                      </Text>
                    </View>
                  </View>
                  <View style={styles.priceBox}>
                    <Text style={styles.priceText}>â‚¬{(item.total_amount * 0.15).toFixed(2)}</Text>
                    <Text style={styles.priceLabel}>guadagno</Text>
                  </View>
                </View>

                {/* Order Details */}
                <View style={styles.orderDetails}>
                  <View style={styles.detailRow}>
                    <Text style={styles.detailLabel}>ğŸ“ Consegna:</Text>
                    <Text style={styles.detailValue} numberOfLines={2}>
                      {item.delivery_address || 'Indirizzo'}
                    </Text>
                  </View>
                  <View style={styles.detailRow}>
                    <Text style={styles.detailLabel}>ğŸ‘¤ Cliente:</Text>
                    <Text style={styles.detailValue}>
                      {item.customer_name || 'Cliente'}
                    </Text>
                  </View>
                  {item.notes && (
                    <View style={styles.detailRow}>
                      <Text style={styles.detailLabel}>ğŸ“ Note:</Text>
                      <Text style={styles.detailValue}>{item.notes}</Text>
                    </View>
                  )}
                </View>

                {/* Status Transition Buttons */}
                {activeTab === 'active' && (
                  <View style={styles.statusButtons}>
                    {item.status === 'accepted' && (
                      <TouchableOpacity
                        style={[styles.statusBtn, { backgroundColor: '#FFA500' }]}
                        onPress={() => updateOrderStatus(item.id, 'pickup')}
                      >
                        <Text style={styles.statusBtnText}>ğŸ“¦ Presa in Carico</Text>
                      </TouchableOpacity>
                    )}
                    {item.status === 'pickup' && (
                      <TouchableOpacity
                        style={[styles.statusBtn, { backgroundColor: '#FFD700' }]}
                        onPress={() => updateOrderStatus(item.id, 'in_transit')}
                      >
                        <Text style={styles.statusBtnText}>ğŸš— In Consegna</Text>
                      </TouchableOpacity>
                    )}
                    {item.status === 'in_transit' && (
                      <TouchableOpacity
                        style={[styles.statusBtn, { backgroundColor: '#28A745' }]}
                        onPress={() => handleCompleteOrder(item)}
                      >
                        <Text style={styles.statusBtnText}>âœ… Consegnato</Text>
                      </TouchableOpacity>
                    )}
                  </View>
                )}

                {/* Action Buttons */}
                <View style={styles.actionButtons}>
                  <TouchableOpacity
                    style={styles.callButton}
                    onPress={() => handleCallCustomer(item.id)}
                  >
                    <Text style={styles.callButtonText}>ğŸ“ Chiama</Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    style={styles.mapButton}
                  >
                    <Text style={styles.mapButtonText}>ğŸ—ºï¸ Mappa</Text>
                  </TouchableOpacity>
                </View>
              </View>
            );
          }}
          refreshing={refreshing}
          onRefresh={loadActiveOrders}
          contentContainerStyle={styles.listContainer}
        />
      )}

      {/* Rating Modal */}
      <Modal visible={ratingModalVisible} transparent animationType="slide">
        <View style={styles.modalOverlay}>
          <View style={styles.modalContent}>
            <Text style={styles.modalTitle}>â­ Valuta il Cliente</Text>
            <Text style={styles.modalSubtitle}>
              Come Ã¨ andato con {selectedOrder?.customer_name}?
            </Text>
            
            <View style={styles.ratingStars}>
              {[1, 2, 3, 4, 5].map(star => (
                <TouchableOpacity
                  key={star}
                  onPress={() => setRating(star)}
                  style={styles.star}
                >
                  <Text style={styles.starText}>
                    {star <= rating ? 'â­' : 'â˜†'}
                  </Text>
                </TouchableOpacity>
              ))}
            </View>

            <TextInput
              style={styles.noteInput}
              placeholder="Aggiungi una nota... (opzionale)"
              value={ratingNote}
              onChangeText={setRatingNote}
              multiline
              placeholderTextColor="#999"
            />

            <View style={styles.modalButtons}>
              <TouchableOpacity
                style={[styles.modalBtn, { backgroundColor: '#ddd' }]}
                onPress={() => {
                  setRatingModalVisible(false);
                  setRating(0);
                  setRatingNote('');
                }}
              >
                <Text style={styles.modalBtnText}>Annulla</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={[styles.modalBtn, { backgroundColor: '#0066FF' }]}
                onPress={submitRating}
              >
                <Text style={[styles.modalBtnText, { color: '#fff' }]}>Salva</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>
    </View>
  );
}

